// https://www.shadertoy.com/view/wd2yRm
// This technique is described in this article I wrote: 
// https://ciphered.xyz/2020/04/08/pixel-sorting-on-shader-using-well-crafted-sorting-filters-glsl/

UNI int resx;
UNI int resy;

UNI int iFrame; // frame number (used for flip-flopping the sets
UNI float fThreshold; // variable threshold for disabling sort per pixel

UNI sampler2D img;
UNI sampler2D sortfield;

IN vec2 texCoord;
{{MODULES_HEAD}}

//

// grayscale average of the colors
float gscale(vec3 c) { return (c.r+c.g+c.b)/3.; }

// get luma value for color sorting
const vec3 LUMA_COEFFICIENTS = vec3(0.299, 0.587, 0.114);
float luma(vec3 c) { return dot(c, LUMA_COEFFICIENTS); }

// Convert RGB to HSL
vec3 rgbToHsl(vec3 color) {
    vec3 hsl;

    float minRGB = min(min(color.r, color.g), color.b);
    float maxRGB = max(max(color.r, color.g), color.b);

    hsl.z = (maxRGB + minRGB) / 2.0;

    float delta = maxRGB - minRGB;

    if (delta == 0.0) {
        hsl.x = 0.0;
        hsl.y = 0.0;
    } else {
        hsl.y = (hsl.z < 0.5) ? (delta / (maxRGB + minRGB)) : (delta / (2.0 - maxRGB - minRGB));

        float deltaR = (((maxRGB - color.r) / 6.0) + (delta / 2.0)) / delta;
        float deltaG = (((maxRGB - color.g) / 6.0) + (delta / 2.0)) / delta;
        float deltaB = (((maxRGB - color.b) / 6.0) + (delta / 2.0)) / delta;

        if (color.r == maxRGB) {
            hsl.x = deltaB - deltaG;
        } else if (color.g == maxRGB) {
            hsl.x = (1.0 / 3.0) + deltaR - deltaB;
        } else if (color.b == maxRGB) {
            hsl.x = (2.0 / 3.0) + deltaG - deltaR;
        }

        if (hsl.x < 0.0) {
            hsl.x += 1.0;
        } else if (hsl.x > 1.0) {
            hsl.x -= 1.0;
        }
    }

    return hsl;
}

// Function to get Hue from RGB color
float getHue(vec3 color) {
    vec3 hsl = rgbToHsl(color);
    return hsl.x;
}


void main()
{    

    vec2 res = vec2(resx, resy); // size in px
    vec2 texel = vec2(1.) / res; // size of 1 px in uv space

    vec2 uv = texCoord; // TouchDesigner uses vUV as the texture coordinate input
    vec2 iuv = floor(uv * res.xy); //pixel coordinate


    vec4 vfield = texture(sortfield, uv);
    vec2 dr = vfield.xy / res.xy; // pixel space to uv space
    vec2 p = uv + dr; // comparator uv

    // we make p loop on x
    if (p.x < 0.0) p.x = 1. - p.x;
    if (p.x > 1.0) p.x = fract(p.x);

    // sample the pixels to test
    vec4 actv = texture(img, uv);
    vec4 comp = texture(img, p);

    // if we are next to a border on the Y-axis, the prevent the sort from happening
    if (uv.y+dr.y < 0.0 || uv.y+dr.y > 1.0) {
    	outColor = actv;
        return;
    }

    // sample the vector field of the texel to compare, to get blue and alpha
    vec4 vfieldComp = texture(sortfield, uv + dr);

    // the alpha component encodes if whether or not a sort is possible
    if (vfield.a < 0.5 || vfieldComp.a < 0.5) {
		outColor = actv;
        return;
    }

    // we can sort the texel with the other one
    vec4 col = actv;
    float gAct = luma(actv.rgb);
    float gCom = luma(comp.rgb);
    //float hAct = getHue(actv.rgb);
    //float hCom = getHue(comp.rgb);



    // we separate the vector field dir into 2 "categories"
    float classed = sign(dr.x*2. + dr.y);

    if (classed < 0.0) {
        // given the blue component value, we sort one way or the other
		if (vfield.b > 0.5) {
			if (gCom > fThreshold && gAct > gCom) {
				col = comp;
			}
		} else {
			if (gAct > fThreshold && gAct < gCom) {
				col = comp;
			}
		}
	} else {
		if (vfield.b > 0.5) {
			if (gAct > fThreshold && gAct < gCom) {
				col = comp;
			}
		} else {
			if (gCom > fThreshold && gAct > gCom) {
				col = comp;
			}
		}
	}


    {{MODULE_COLOR}}
    outColor = col;

}

